        and t4iah.ATTRIBUTE11 = #{attribute11,jdbcType = VARCHAR}


        and t4iah.ATTRIBUTE12 = #{attribute12,jdbcType = VARCHAR}


        and t4iah.ATTRIBUTE13 = #{attribute13,jdbcType = VARCHAR}


        and t4iah.ATTRIBUTE14 = #{attribute14,jdbcType = VARCHAR}


        and t4iah.ATTRIBUTE15 = #{attribute15,jdbcType = VARCHAR}


        and t4iah.ATTRIBUTE2 = #{attribute2,jdbcType = VARCHAR}


        and t4iah.ATTRIBUTE3 = #{attribute3,jdbcType = VARCHAR}


        and t4iah.ATTRIBUTE4 = #{attribute4,jdbcType = VARCHAR}


        and t4iah.ATTRIBUTE5 = #{attribute5,jdbcType = VARCHAR}


        and t4iah.ATTRIBUTE6 = #{attribute6,jdbcType = VARCHAR}


