        and t4iah.total_amount = #{totalAmount,jdbcType = OTHER}

select

from t47360_invoice_apply_header t4iah


        AND t4iah.apply_header_number BETWEEN #{applyHeaderNumberFrom} AND #{applyHeaderNumberTo}


        AND t4iah.creation_date BETWEEN #{creationDateFrom} AND #{creationDateTo}


        AND t4iah.submit_time BETWEEN #{submitTimeFrom} AND #{submitTimeTo}


        AND t4iah.apply_status IN

            #{status}



        AND t4iah.invoice_type = #{invoiceType}
